import React from 'react'
import { Card, CardTitle } from 'react-materialize'

const Artiste = ({ groupe, index }) => {
  return (
    <Card
      key={index}
      className="artiste-card"
      header={<CardTitle image={groupe.image != null ? `./img/groupes/${groupe.image}` : null} />}
      title={
        groupe.heure != null && groupe.heure !== ''
          ? `${groupe.nom} - ${groupe.heure}`
          : `${groupe.nom}`
      }
      actions={
        groupe.site != null &&
          groupe.facebook != null
          ? [
            <a
              href={groupe.site}
              target="_blank"
              rel="external noopener noreferrer"
            >
              Site
                </a>,
            <a
              href={groupe.facebook}
              target="_blank"
              rel="external noopener noreferrer"
            >
              Facebook
                </a>
          ]
          : groupe.site != null
            ? [
              <a
                href={groupe.site}
                target="_blank"
                rel="external noopener noreferrer"
              >
                Site
                </a>
            ]
            : groupe.facebook != null
              ? [<a href={groupe.facebook}>Facebook</a>]
              : []
      }
    >
      {groupe.description}
    </Card>
  )
}

export default Artiste
