import React, { Component } from "react";
import { Card, CardTitle } from "react-materialize";

import "./Artiste.css";

class Artiste extends Component {
  render() {
    return (
      <Card
        header={<CardTitle image={this.props.groupe.image} />}
        title={
          this.props.groupe.heure != null && this.props.groupe.heure !== ""
            ? `${this.props.groupe.nom} - ${this.props.groupe.heure}`
            : `${this.props.groupe.nom}`
        }
        actions={
          this.props.groupe.site !== undefined &&
          this.props.groupe.facebook !== undefined
            ? [
                <a href={this.props.groupe.site} target="_blank" rel="external">
                  Site
                </a>,
                <a href={this.props.groupe.facebook}>Facebook</a>
              ]
            : this.props.groupe.site !== undefined
            ? [
                <a href={this.props.groupe.site} target="_blank" rel="external">
                  Site
                </a>
              ]
            : this.props.groupe.facebook !== undefined
            ? [<a href={this.props.groupe.facebook}>Facebook</a>]
            : []
        }
      >
        {this.props.groupe.description}
      </Card>
    );
  }
}

export default Artiste;
