import React, { Component } from 'react'
import { Card, CardTitle } from 'react-materialize'

import './Artiste.css'

class Artiste extends Component {
  constructor (props) {
    super(props)
    console.log(this.props.groupe)
  }
  render () {
    return (
      <Card
        header={<CardTitle image={this.props.groupe.image}>{this.props.groupe.heure}</CardTitle>}
        title={this.props.groupe.nom}
        actions={
          this.props.groupe.site !== undefined && this.props.groupe.facebook ? [
            <a href={this.props.groupe.site} target='_blank' rel='external'>Site</a>,
            <a href={this.props.groupe.facebook}>Facebook</a>
          ] : this.props.groupe.site !== undefined ? [
            <a href={this.props.groupe.site} target='_blank' rel='external'>Site</a>
          ] : [<a href={this.props.groupe.facebook}>Facebook</a>]
        }>
        {this.props.groupe.description}
      </Card>
    )
  }
}

export default Artiste
