import React from 'react'
import { Card, CardTitle } from 'react-materialize'

const Artiste = ({ groupe }) => {
  const actions = []
  groupe.site != null && actions.push(<a
    key={`site-${groupe.nom}`}
    href={groupe.site}
    target="_blank"
    rel="external noopener noreferrer"
  >
    Site
  </a>)

  groupe.facebook != null && actions.push(<a
    key={`fb-${groupe.nom}`}
    href={groupe.facebook}
    target="_blank"
    rel="external noopener noreferrer"
  >
    Facebook
  </a>)


  return (
    <Card
      key={index}
      className="artiste-card"
      header={<CardTitle image={groupe.image != null ? `./img/groupes/${groupe.image}` : null} />}
      title={
        groupe.heure != null && groupe.heure !== ''
          ? `${groupe.nom} - ${groupe.heure}`
          : `${groupe.nom}`
      }
      actions={
        actions
      }
    >
      {groupe.description}

    </Card>
  )
}

export default Artiste
